function appendText(e,t){document.getElementById(e).innerHTML=t}fetch("https://whatismyip.tusmanakhter.com/api").then(e=>e.json()).then(function(e){const t=`Your IP address is <b>${e.ip}</b>`,n=`${e.city}, ${e.region}, ${e.country}`;appendText("ip",t),appendText("location",n)}).catch(function(e){console.log(e),appendText("ip","Failed to retrieve your IP address")});